mapper.select:
SELECT B.BOARD_SEQ, B.BOARD_TYPE, B.TITLE, B.CONTENTS, B.REG_DATE, B.DEL_YN FROM T_BOARD B
AND B.TITLE LIKE CONCAT('%', #{parameter.keyword}, '%')
AND B.BOARD_TYPE IN (
#{value}
)
ORDER BY B.REG_DATE DESC LIMIT #{pageRequest.limit}, #{pageRequest.offset}

mapper.update:
UPDATE T_BOARD
SET 
	TITLE = #{title},
	CONTENTS = #{contents}
WHERE BOARD_SEQ = #{boardSeq}
